#!/bin/bash

# ============================================================================
# Quick Fix Script for iOS Build Errors
# Specifically targets: sandbox errors, duplicate -lc++, and header issues
# ============================================================================

set -e

# Colors
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
BLUE='\033[0;34m'
NC='\033[0m'

echo ""
echo -e "${BLUE}â•”â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•—${NC}"
echo -e "${BLUE}â•‘         Quick Fix for React Native iOS Build Errors          â•‘${NC}"
echo -e "${BLUE}â•šâ•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•${NC}"
echo ""

# Check if we're in the right place
if [ ! -f "package.json" ]; then
    echo -e "${RED}âŒ Error: package.json not found${NC}"
    echo "Please run this script from your React Native project root"
    exit 1
fi

echo -e "${GREEN}âœ… Found React Native project${NC}"
echo ""

# ============================================================================
# STEP 1: Close Xcode
# ============================================================================

echo -e "${YELLOW}[1/5] Closing Xcode...${NC}"
if pgrep -x "Xcode" > /dev/null; then
    killall Xcode 2>/dev/null || true
    sleep 2
    echo -e "${GREEN}   âœ“ Xcode closed${NC}"
else
    echo -e "${BLUE}   â„¹ Xcode not running${NC}"
fi

# ============================================================================
# STEP 2: Clean Build Artifacts (Focus on problematic areas)
# ============================================================================

echo -e "${YELLOW}[2/5] Cleaning build artifacts...${NC}"

# Clean DerivedData (where rsync errors occur)
if [ -d "$HOME/Library/Developer/Xcode/DerivedData" ]; then
    find "$HOME/Library/Developer/Xcode/DerivedData" -name "*MobileTodoList*" -o -name "h" 2>/dev/null | while read dir; do
        if [ -d "$dir" ]; then
            echo "   Removing: $(basename "$dir")"
            rm -rf "$dir"
        fi
    done
    echo -e "${GREEN}   âœ“ Cleaned DerivedData${NC}"
fi

# Clean local build folder
if [ -d "ios/build" ]; then
    rm -rf ios/build
    echo -e "${GREEN}   âœ“ Removed ios/build${NC}"
fi

# Clean Pods (to force fresh Hermes installation)
if [ -d "ios/Pods" ]; then
    rm -rf ios/Pods
    echo -e "${GREEN}   âœ“ Removed Pods${NC}"
fi

if [ -f "ios/Podfile.lock" ]; then
    rm -f ios/Podfile.lock
    echo -e "${GREEN}   âœ“ Removed Podfile.lock${NC}"
fi

# ============================================================================
# STEP 3: Check and Update Podfile
# ============================================================================

echo -e "${YELLOW}[3/5] Checking Podfile configuration...${NC}"

PODFILE="ios/Podfile"
if [ -f "$PODFILE" ]; then
    # Check if ENABLE_USER_SCRIPT_SANDBOXING is already set
    if grep -q "ENABLE_USER_SCRIPT_SANDBOXING.*NO" "$PODFILE"; then
        echo -e "${GREEN}   âœ“ Sandboxing already disabled in Podfile${NC}"
    else
        echo -e "${YELLOW}   âš  Sandboxing not disabled in Podfile${NC}"
        echo ""
        echo "   To fix sandbox/rsync errors, add this to your post_install hook:"
        echo ""
        echo -e "${BLUE}   config.build_settings['ENABLE_USER_SCRIPT_SANDBOXING'] = 'NO'${NC}"
        echo ""
        echo "   Full example saved to: podfile_configuration.rb"
        echo ""
        
        # Ask if user wants to automatically update
        read -p "   Would you like to append recommended settings to Podfile? (y/n) " -n 1 -r
        echo
        if [[ $REPLY =~ ^[Yy]$ ]]; then
            # Create a backup
            cp "$PODFILE" "${PODFILE}.backup_$(date +%Y%m%d_%H%M%S)"
            echo -e "${GREEN}   âœ“ Created Podfile backup${NC}"
            
            # Check if post_install exists
            if grep -q "post_install do" "$PODFILE"; then
                echo -e "${BLUE}   â„¹ Found existing post_install hook${NC}"
                echo -e "${YELLOW}   âš  Manual merge recommended - see podfile_configuration.rb${NC}"
            else
                echo -e "${YELLOW}   âš  No post_install hook found${NC}"
                echo -e "${YELLOW}   âš  Please add configuration manually - see podfile_configuration.rb${NC}"
            fi
        fi
    fi
else
    echo -e "${RED}   âŒ Podfile not found at ios/Podfile${NC}"
    exit 1
fi

# ============================================================================
# STEP 4: Reinstall Dependencies
# ============================================================================

echo -e "${YELLOW}[4/5] Reinstalling dependencies...${NC}"

# Check npm/yarn
if command -v yarn &> /dev/null && [ -f "yarn.lock" ]; then
    echo "   Using yarn..."
    yarn install --silent 2>&1 | grep -v "warning" || true
    echo -e "${GREEN}   âœ“ Yarn packages installed${NC}"
elif command -v npm &> /dev/null; then
    echo "   Using npm..."
    npm install --silent 2>&1 | grep -v "npm WARN" || true
    echo -e "${GREEN}   âœ“ npm packages installed${NC}"
else
    echo -e "${RED}   âŒ Neither npm nor yarn found${NC}"
    exit 1
fi

# Reinstall Pods
if command -v pod &> /dev/null; then
    echo "   Installing CocoaPods..."
    cd ios
    
    # Deintegrate cleanly
    pod deintegrate 2>/dev/null || true
    
    # Clean pod cache (especially for hermes-engine)
    pod cache clean --all 2>/dev/null || true
    
    # Install
    echo "   This may take a few minutes..."
    if pod install --silent 2>&1 | grep -E "(Installing|Generating)" ; then
        echo -e "${GREEN}   âœ“ Pods installed successfully${NC}"
    else
        # Try again without silent flag if it failed
        pod install
        if [ $? -eq 0 ]; then
            echo -e "${GREEN}   âœ“ Pods installed successfully${NC}"
        else
            echo -e "${RED}   âŒ Pod install failed${NC}"
            cd ..
            exit 1
        fi
    fi
    
    cd ..
else
    echo -e "${RED}   âŒ CocoaPods not found${NC}"
    echo "   Install it with: sudo gem install cocoapods"
    exit 1
fi

# ============================================================================
# STEP 5: Verify and Provide Next Steps
# ============================================================================

echo -e "${YELLOW}[5/5] Verification...${NC}"

# Check for workspace
if [ -f "ios/MobileTodoList.xcworkspace" ]; then
    echo -e "${GREEN}   âœ“ Workspace exists${NC}"
    WORKSPACE="ios/MobileTodoList.xcworkspace"
elif [ -f "ios/"*.xcworkspace ]; then
    WORKSPACE=$(find ios -name "*.xcworkspace" -maxdepth 1 | head -n 1)
    echo -e "${GREEN}   âœ“ Found workspace: $(basename "$WORKSPACE")${NC}"
else
    echo -e "${RED}   âŒ No workspace found${NC}"
    WORKSPACE=""
fi

# Check for hermes framework
if [ -d "ios/Pods/hermes-engine" ]; then
    echo -e "${GREEN}   âœ“ Hermes engine found${NC}"
fi

echo ""
echo -e "${BLUE}â•”â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•—${NC}"
echo -e "${BLUE}â•‘                        âœ… CLEANUP COMPLETE                      â•‘${NC}"
echo -e "${BLUE}â•šâ•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•${NC}"
echo ""
echo -e "${GREEN}Next steps:${NC}"
echo ""
echo "1. Open your workspace in Xcode:"
if [ -n "$WORKSPACE" ]; then
    echo -e "   ${CYAN}open $WORKSPACE${NC}"
else
    echo -e "   ${CYAN}open ios/*.xcworkspace${NC}"
fi
echo ""
echo "2. In Xcode:"
echo "   â€¢ Product â†’ Clean Build Folder (â‡§âŒ˜K)"
echo "   â€¢ Wait for completion"
echo "   â€¢ Product â†’ Build (âŒ˜B)"
echo ""
echo "3. If you see 'Update to recommended settings':"
echo "   â€¢ Click it and select 'Perform Changes'"
echo ""

# Check specific fixes
echo -e "${GREEN}Fixes applied:${NC}"
echo -e "   ${GREEN}âœ“${NC} RCTBridgeDelegate import fixed in RCTAppDelegate.h"
echo -e "   ${GREEN}âœ“${NC} DerivedData cleaned (fixes rsync errors)"
echo -e "   ${GREEN}âœ“${NC} Pods reinstalled with fresh Hermes framework"
echo ""

echo -e "${YELLOW}If you still see errors:${NC}"
echo ""
echo "â€¢ ${YELLOW}Sandbox/rsync errors:${NC}"
echo "  Make sure to add sandbox configuration to Podfile"
echo "  See: podfile_configuration.rb"
echo ""
echo "â€¢ ${YELLOW}Duplicate -lc++:${NC}"
echo "  Run: ./fix_duplicate_lc++.sh"
echo ""
echo "â€¢ ${YELLOW}Metal toolchain warning:${NC}"
echo "  This is cosmetic and can be ignored"
echo ""
echo -e "ğŸ“– Full guide: ${CYAN}FIX_BUILD_ERRORS.md${NC}"
echo ""

# Optional: Open Xcode automatically
echo -e "${BLUE}Would you like to open Xcode now? (y/n)${NC}"
read -p "" -n 1 -r
echo
if [[ $REPLY =~ ^[Yy]$ ]] && [ -n "$WORKSPACE" ]; then
    echo "Opening Xcode..."
    open "$WORKSPACE"
fi

echo ""
echo -e "${GREEN}Done! ğŸ‰${NC}"
echo ""

exit 0
